import React, { useState, useEffect } from 'react';
import { Button, Container, Table, Card, InputGroup, Form, Modal } from 'react-bootstrap';
import axios from 'axios';

export default function Student() {
    const [students, setStudents] = useState([]);
    const [search, setSearch] = useState('');
    const [showModal, setShowModal] = useState(false);
    const [editId, setEditId] = useState(null);
    const [formData, setFormData] = useState({
        Student_key: '',
        Student_name: '',
        Subject_key: '',
        Grade: '',
        Remarks: ''
    });

    const fetchStudents = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/students');
            setStudents(response.data);
        } catch (err) {
            console.error('Error fetching students:', err);
        }
    };

    useEffect(() => {
        fetchStudents();
    }, []);

    const handleShow = () => setShowModal(true);
    const handleClose = () => {
        setShowModal(false);
        setEditId(null);
        setFormData({ Student_key: '', Student_name: '', Subject_key: '', Grade: '', Remarks: '' });
    };

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (editId) {
                await axios.put(`http://localhost:8080/api/students/${editId}`, formData);
            } else {
                await axios.post('http://localhost:8080/api/students', formData);
            }
            handleClose();
            fetchStudents();
        } catch (err) {
            console.log('Error saving student:', err);
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this student?')) {
            try {
                await axios.delete(`http://localhost:8080/api/students/${id}`);
                fetchStudents(); // refresh the list
            } catch (err) {
                console.error('Error deleting student:', err);
            }
        }
    };

    const filteredStudents = students.filter(student =>
        student.Student_name.toLowerCase().includes(search.toLowerCase())
    );

    return (
        <Container style={{ marginTop: '3%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <Card style={{ width: '90%', padding: '30px', boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)', borderRadius: '20px', backgroundColor: '#ffffff' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
                    <h2 style={{ fontWeight: '600', color: '#1e3a8a' }}>ðŸŽ“ Student Management</h2>
                    <Button variant="success" style={{ fontWeight: 500 }} onClick={handleShow}>+ Add New</Button>
                </div>

                <InputGroup className="mb-4" style={{ maxWidth: '400px' }}>
                    <Form.Control placeholder="Search by student name..." value={search} onChange={(e) => setSearch(e.target.value)} />
                    <Button variant="primary">Search</Button>
                </InputGroup>

                <Table striped bordered hover responsive>
                    <thead style={{ backgroundColor: '#1e3a8a', color: 'white', textAlign: 'center' }}>
                        <tr>
                            <th>Student Name</th>
                            <th>Subject Name</th>
                            <th>Grade</th>
                            <th>Remarks</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody style={{ textAlign: 'center', verticalAlign: 'middle' }}>
                        {filteredStudents.map(student => (
                            <tr key={student._id}>
                                <td>{student.Student_name}</td>
                                <td>{student.Subject_key}</td>
                                <td>{student.Grade}</td>
                                <td>{student.Remarks}</td>
                                <td>
                                    <Button variant="outline-primary" size="sm" className="me-2">Edit</Button>
                                    <Button
                                        variant="outline-danger"
                                        size="sm"
                                        onClick={() => handleDelete(student._id)}
                                    >
                                        Delete
                                    </Button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </Card>

            <Modal show={showModal} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Add New Student</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group className="mb-3" controlId="Student_key">
                            <Form.Label>Student Key</Form.Label>
                            <Form.Control
                                type="text"
                                name="Student_key"
                                value={formData.Student_key}
                                onChange={handleChange}
                                required
                            />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="Student_name">
                            <Form.Label>Student Name</Form.Label>
                            <Form.Control
                                type="text"
                                name="Student_name"
                                value={formData.Student_name}
                                onChange={handleChange}
                                required
                            />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="Subject_key">
                            <Form.Label>Subject Name</Form.Label>
                            <Form.Control
                                type="text"
                                name="Subject_key"
                                value={formData.Subject_key}
                                onChange={handleChange}
                                required
                            />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="Grade">
                            <Form.Label>Grade</Form.Label>
                            <Form.Control
                                type="number"
                                name="Grade"
                                value={formData.Grade}
                                onChange={(e) => {
                                    const value = e.target.value;
                                    let remarks = '';
                                    if (value !== '') {
                                        remarks = Number(value) >= 75 ? 'PASS' : 'FAIL';
                                    }
                                    setFormData({
                                        ...formData,
                                        Grade: value,
                                        Remarks: remarks,
                                    });
                                }}
                                required
                            />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="Remarks">
                            <Form.Label>Remarks</Form.Label>
                            <Form.Control
                                type="text"
                                name="Remarks"
                                value={formData.Remarks}
                                readOnly
                            />
                        </Form.Group>

                        <Button variant="primary" type="submit">
                            Add Student
                        </Button>
                    </Form>
                </Modal.Body>
            </Modal>
        </Container>
    );
}
