import React, { useState } from 'react';
import { Button, Container, Table, Card, InputGroup, Form, Modal } from 'react-bootstrap';
import axios from 'axios';

export default function Student() {
    const [search, setSearch] = useState('');
    const [showModal, setShowModal] = useState(false);

    const [formData, setFormData] = useState({
        Student_key: '',
        Student_name: '',
        Subject_key: '',
        Grade: '',
        Remarks: ''
    });

    const handleShow = () => setShowModal(true);
    const handleClose = () => {
        setShowModal(false);
        setFormData({ Student_name: '', Subject_key: '', Grade: '', Remarks: '' });
    };

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/api/students', formData);
            console.log('Student added:', response.data);
            handleClose();
        } catch (err) {
            console.error('Error adding student:', err);
        }
    };


    return (
        <Container
            style={{
                marginTop: '3%',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
            }}
        >
            <Card
                style={{
                    width: '90%',
                    padding: '30px',
                    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
                    borderRadius: '20px',
                    backgroundColor: '#ffffff',
                }}
            >
                <div
                    style={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center',
                        marginBottom: '20px',
                    }}
                >
                    <h2 style={{ fontWeight: '600', color: '#1e3a8a' }}>ðŸŽ“ Student Management</h2>
                    <Button variant="success" style={{ fontWeight: 500 }}>
                        + Add New
                    </Button>
                </div>

                <InputGroup className="mb-4" style={{ maxWidth: '400px' }}>
                    <Form.Control
                        placeholder="Search by student name..."
                        value={search}
                        onChange={(e) => setSearch(e.target.value)}
                    />
                    <Button variant="primary">Search</Button>
                </InputGroup>

                <Table striped bordered hover responsive>
                    <thead
                        style={{
                            backgroundColor: '#1e3a8a',
                            color: 'white',
                            textAlign: 'center',
                        }}
                    >
                        <tr>
                            <th>Student Name</th>
                            <th>Subject Name</th>
                            <th>Grade</th>
                            <th>Remarks</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody style={{ textAlign: 'center', verticalAlign: 'middle' }}>
                        {/* Sample rows */}
                        <tr>
                            <td>John Doe</td>
                            <td>Mathematics</td>
                            <td>A</td>
                            <td>Excellent performance</td>
                            <td>
                                <Button variant="outline-primary" size="sm" className="me-2">
                                    Edit
                                </Button>
                                <Button variant="outline-danger" size="sm">
                                    Delete
                                </Button>
                            </td>
                        </tr>
                        <tr>
                            <td>Jane Smith</td>
                            <td>Science</td>
                            <td>B+</td>
                            <td>Good progress</td>
                            <td>
                                <Button variant="outline-primary" size="sm" className="me-2">
                                    Edit
                                </Button>
                                <Button variant="outline-danger" size="sm">
                                    Delete
                                </Button>
                            </td>
                        </tr>
                    </tbody>
                </Table>
            </Card>

            {/* ===== Add Student Modal ===== */}
            <Modal show={showModal} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Add New Student</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group className="mb-3" controlId="Student_name">
                            <Form.Label>Student Name</Form.Label>
                            <Form.Control
                                type="text"
                                name="Student_name"
                                value={formData.Student_name}
                                onChange={handleChange}
                                required
                            />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="Subject_key">
                            <Form.Label>Subject Name</Form.Label>
                            <Form.Control
                                type="text"
                                name="Subject_key"
                                value={formData.Subject_key}
                                onChange={handleChange}
                                required
                            />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="Grade">
                            <Form.Label>Grade</Form.Label>
                            <Form.Control
                                type="text"
                                name="Grade"
                                value={formData.Grade}
                                onChange={handleChange}
                                required
                            />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="Remarks">
                            <Form.Label>Remarks</Form.Label>
                            <Form.Control
                                type="text"
                                name="Remarks"
                                value={formData.Remarks}
                                onChange={handleChange}
                            />
                        </Form.Group>

                        <Button variant="primary" type="submit">
                            Add Student
                        </Button>
                    </Form>
                </Modal.Body>
            </Modal>

        </Container>
    );
}
